<?phpnamespace Admin\Controller;/** * 消费详情类 * Class AccountController * @package Admin\Controller */class AccountController extends AdminCoreController{    public function _initialize()    {        parent::_initialize();        $this->_mod=D('Account');        $this->set_mod('Account');                $this->_member=D('Member');    }    public function index() {        $map = $this->_search();        $mod = $this->_mod;        !empty($mod) && $this->_list($mod, $map);        $this->display();    }    public function _search()    {        $map = [];        ($uid = I('uid','','intval'))   && $map['uid']          = $uid;//用户或商家id        ($type = I('type','','intval'))   && $map['type']        = $type;        ($time_start = I('time_start')) && $map['add_time'][]   = ['egt',strtotime($time_start)];        ($time_end = I('time_end'))     && $map['add_time'][]   = ['elt',strtotime($time_end) + 86399];        $this->assign('search',[            'time_start'    => $time_start,            'time_end'      => $time_end,        ]);        !empty($uid) && $this->assign('uid',$uid);        return $map;    }            public function _before_index() {		$uid=I('uid');//用户id        $member=$this->_member->where(['id'=>$uid])->field('nickname,mobile')->find();   		$this->assign('member',$member);    }}